const path = require("path");

const app = express();

//file upload folder
const UPLOAD_FOLDER = __dirname + "/uploads/";

//file storage
const storage = multer.diskStorage({
  destination: (req, file, cb) => {
    cb(null, UPLOAD_FOLDER);
  },

  filename: (req, file, cb) => {
    const fileExt = path.extname(file.originalname);
    const filename =
      file.originalname
        .replace(fileExt, "")
        .toLowerCase()
        .split(" ")
        .join("-") +
      "-" +
      Date().now();
    cb(null, filename + fileExt);
  },
});

const upload = multer({
  storage: storage,
  //file size in bytes
  limits: {
    fileSize: 1000000,
  },
  fileFilter: function (req, file, cb) {
    if (file.fieldname === "avatar") {
      if (
        file.mimetype === "image/png" ||
        file.mimetype === "image/jpg" ||
        file.mimetype === "image/jpeg"
      ) {
        cb(null, true);
      } else {
        cb(null, false);
      }
    } else if (file.fieldname === "doc") {
      if (file.mimetype === "application/pdf") {
        cb(new Error("Only allow png, jpg, or jepg files"));
      } else {
        cb(new Error("Only pdf files"));
      }
    } else {
      cb(null, false);
    }
  },
});

//single file upload
// app.post("/", upload.single("upload"), (req, res) => {
//   res.send("hello");
// });

//multiple file upload
// app.post("/", upload.single("avatar"), (req, res) => {
//   res.send("hello");
// });

//multiple feild upload file
app.post(
  "/",
  upload.fields([
    { name: "avatar", maxCount: 4 },
    { name: "doc", maxCount: 1 },
  ]),
  (req, res) => {
    res.send("hello");
  },
);

//default error handler
app.use((err, req, res, next) => {
  if (err) {
    if (err instanceof multer.MulterError) {
      res.status(500).send(err + "File Upload Error");
    } else {
      res.status(500).send(err.message);
    }
  }
});

app.listen(8080, () => {
  console.log("Hello World!");
});
