       const editUser = async (id, user) => {
  try {
    const updatedUser = await User.findOneAndUpdate(
      { _id: id },
      { $set: user },
      { new: true },
    );
    return updatedUser;
  } catch (error) {
    throw new Error(error);
  }
};

